    void TmFramerComponentBase ::
      m_p_comStatusIn_in(
          Fw::PassiveComponentBase* callComp,
          FwIndexType portNum,
          Fw::Success& condition
      )
    {
      FW_ASSERT(callComp);
      TmFramerComponentBase* compPtr = static_cast<TmFramerComponentBase*>(callComp);
      compPtr->comStatusIn_handlerBase(
        portNum,
        condition
      );
    }

