  // ----------------------------------------------------------------------
  // Helper functions for dispatching current messages
  // ----------------------------------------------------------------------

  Fw::QueuedComponentBase::MsgDispatchStatus SignalGenComponentBase ::
    dispatchCurrentMessages()
  {
    // Dispatch all current messages unless ERROR or EXIT occur
    const FwSizeType currentMessageCount = this->m_queue.getMessagesAvailable();
    MsgDispatchStatus messageStatus = MsgDispatchStatus::MSG_DISPATCH_EMPTY;
    for (FwSizeType i = 0; i < currentMessageCount; i++) {
      messageStatus = this->doDispatch();
      if (messageStatus != QueuedComponentBase::MSG_DISPATCH_OK) {
        break;
      }
    }
    return messageStatus;
  }

  // ----------------------------------------------------------------------
  // Calls for messages received on special input ports
  // ----------------------------------------------------------------------

  void SignalGenComponentBase ::
    m_p_cmdIn_in(
        Fw::PassiveComponentBase* callComp,
        FwIndexType portNum,
        FwOpcodeType opCode,
        U32 cmdSeq,
        Fw::CmdArgBuffer& args
    )
  {
    FW_ASSERT(callComp);
    SignalGenComponentBase* compPtr = static_cast<SignalGenComponentBase*>(callComp);

    const U32 idBase = callComp->getIdBase();
    FW_ASSERT(opCode >= idBase, static_cast<FwAssertArgType>(opCode), static_cast<FwAssertArgType>(idBase));

    // Select base class function based on opcode
    switch (opCode - idBase) {
      case OPCODE_SETTINGS: {
        compPtr->Settings_cmdHandlerBase(
          opCode,
          cmdSeq,
          args
        );
        break;
      }

      case OPCODE_TOGGLE: {
        compPtr->Toggle_cmdHandlerBase(
          opCode,
          cmdSeq,
          args
        );
        break;
      }

      case OPCODE_SKIP: {
        compPtr->Skip_cmdHandlerBase(
          opCode,
          cmdSeq,
          args
        );
        break;
      }

      case OPCODE_DP: {
        compPtr->Dp_cmdHandlerBase(
          opCode,
          cmdSeq,
          args
        );
        break;
      }
    }
  }

